mapper.select:
select TravelID, TravelName, Attention, KeyWord, CreatTime, Guide, SportTypeName,TravelPicture, tti.SportTypeID from triptravelinfo tti, basicsporttype bstp
tti.SportTypeID = bstp.SportTypeID
and TravelName = #{tripTravelInfo.travelname,jdbcType=VARCHAR}
and tti.SportTypeID = bstp.SportTypeID and tti.SportTypeID = #{tripTravelInfo.sporttypeid,jdbcType=CHAR}
and KeyWord = #{keyword,jdbcType=VARCHAR}
limit #{page.startIndex},#{page.pageSize}

select COUNT(TravelID) from triptravelinfo tti, basicsporttype bstp
tti.SportTypeID = bstp.SportTypeID
and TravelName = #{travelname,jdbcType=VARCHAR}
and tti.SportTypeID = bstp.SportTypeID and tti.SportTypeID = #{sporttypeid,jdbcType=CHAR}
and KeyWord = #{keyword,jdbcType=VARCHAR}

mapper.insert:
insert into triptravelinfo (TravelID, TravelName, SportTypeID, 
  Attention, KeyWord, CreatTime, Guide,TravelPicture, Enabled
  )
values (#{travelid,jdbcType=CHAR}, #{travelname,jdbcType=VARCHAR}, #{sporttypeid,jdbcType=CHAR}, 
  #{attention,jdbcType=VARCHAR}, #{keyword,jdbcType=CHAR}, #{creattime,jdbcType=TIMESTAMP}, #{guide,jdbcType=VARCHAR},#{travelpicture,jdbcType=VARCHAR}, #{enabled,jdbcType=CHAR}
  )
